#include <iomanip>
#include <iostream>
#include <string>

using namespace std;

class Solution {
  public:
    static bool canConstruct(const string s, const int k) noexcept {
        if (s.size() == k)
            return true;
        else if (s.size() < k)
            return false;

        int charset[26]{}, odd{}, even{};

        for (const char ch : s)
            ++charset[ch - 'a'];

        for (const int v : charset)
            if (v != 0)
                v % 2 ? ++odd : ++even;

        if (odd > k)
            return false;
        else
            return true;
    }
};

// clang-format off
int main() {
    using namespace placeholders;
    auto test = bind(Solution::canConstruct, _1, _2);
    test("annabelle", 2);                   // true
    test("leetcode", 3);                    // false
    test("true", 4);                        // true
    test("ab", 10);                         // false
    test("a", 1);                           // true
    test("yiipem", 3);                      // false
    test("abcdefghijklmnopqrstuvwxyz", 25); // false
    test("rraceca", 3);                     // true
    // test("",
    //      30);                            // true
    // test("",
    //      4659);                          // true
    test("fikcrbqhrtwjnkpmwssynwhnwlzewbnailuuudgbrriwiqvxbiehoizbiroaobhmzhhvxkggldhzbyyzalzidkoafjuuvgicpvnupvhzpcjnhlwrossorwlhnjcpzhvpunvpcigvuujfaokdizlazyybzhdlggkxvhhzmhboaoribzioheibxvqiwirrbgduuulianbwezlwnhwnysswmpknjwtrhqbrckif",
         126);             // testcase  90/108 true
    test("messi", 3);      // testcase 105/108 true
    test("tbfgfzycjpmkxcagjirlgvrtklsudofoxhobdldfbibaqymzhqaafcjuwvgucgbiycejkxdzbwghtqxshzziileklbjvfkxvplhmwbspdfexyphbftqsbkq",
         23);              // testcase 107/108 true
}
