#include "../termcolor.h"
#include <functional>
#include <iostream>
#include <stack>
#include <string>

using namespace std;

class Solution {
  public:
    static int minAddToMakeValid(string s) noexcept {
        stack<char> sk;
        int count{};
        for (size_t i{}; i < s.size(); ++i) {
            if (sk.empty())
                sk.push(s[i++]);

            if (i == s.size())
                break;

            if (sk.top() == '(' && s[i] == ')')
                sk.pop();
            else
                sk.push(s[i]);
        }
        return sk.size();
    }

    static int cls_minAddToMakeValid(string s) noexcept {
        int res{};
        int numOpen{};

        for (char c : s) {
            if (c == '(')
                ++numOpen;
            else
                numOpen > 0 ? --numOpen : ++res;
        }
        return numOpen + res;
    }
};

function<char(void)> init = []() -> char {
    std::ios::sync_with_stdio(false);
    std::cin.tie(0);
    std::cout.tie(0);
    return 'c';
};
